var rref = require('../');
var test = require('tape');

test(function (t) {
    t.plan(2);
    
    t.deepEqual(
        rref([
            [ 1, 2, -1, -4],
            [ 2, 3, -1,-11],
            [-2, 0, -3, 22]
        ]),
        [
            [ 1, 0, 0, -8 ],
            [ 0, 1, 0, 1 ],
            [ 0, 0, 1, -2 ]
        ]
    );
    t.deepEqual(
        rref([
            [ 2, 1, -1, 8 ],
            [ -3, -1, 2, -11 ],
            [ -2, 1, 2, -3 ]
        ]),
        [
            [ 1, 0, 0, 2 ],
            [ 0, 1, 0, 3 ],
            [ 0, 0, 1, -1 ]
        ]
    );
});
